package com.syntax.class02;

public class IncrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=10;
System.out.println(number);
number=number+1;
number=number+number;
System.out.println(number);

number++; //increments value by 1

System.out.println(number);

number=10;
number++;
System.out.println(number);
	}

}
