package com.syntax.class06;

public class LoopsIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int time=14;
if (time<17) {
	System.out.println("It is day");
}
while (time<17) {
	System.out.println("It is day");
	time++;
	
}

System.out.println("I want to print numbers from 1 to 20");
int i=1;
while(i<=20) {
	System.out.println(i);
	i++;
}

System.out.println("I want to print numbers from 50 to 1");
int y=50;
while (y>=1) {
	
	System.out.println(y);
y--;

	}
System.out.println("I want to print even numbers from 1 to 10 ");

int x=2;
while(x<=10) {
	System.out.println(x);
	x+=2;
}
System.out.println(x);
}

}


